    {
      "action": "createEncounter",
      "parameters": {
        "player": "Norn",
        "npcs": [],
        "monsters": ["Specter"],
        "encounterSummary": "Norn approaches the altar without performing a gesture of respect or making an offering. The vengeful specter of the fallen knight materializes from the shadows and attacks in the Ruined Chapel."
      }
    },
    {
      "action": "updateTime",
      "parameters": {
        "timeEstimate": 1
      }
