      "parameters": {
        "characterName": "eirik_hearthwise",
        "changes": "Gained 25 XP for defeating bandits at North Tower Overlook. Total XP now 25/300."
      }
    },
    {
      "action": "updateCharacterInfo",
      "parameters": {
        "characterName": "Scout Kira",
        "changes": "Gained 25 XP for defeating bandits at North Tower Overlook. Total XP now 25/300."
      }
    },
    {
      "action": "updateTime",
